mapper.update:

create table ${table} (
    _MID int  unsigned auto_increment primary key,
    time      timestamp not null,
    direction enum('In', 'Out') not null
);

mapper.insert:

insert into ${table}
(time, direction

    , type, senderId, senderName

) value
(#{message.time}, #{message.direction}

    , #{message.type}, #{message.senderId}, #{message.senderName}

)
